import{gsap}from"gsap";import{DrawSVGPlugin}from"gsap/DrawSVGPlugin";import{GSDevTools}from"gsap/GSDevTools";function simpleMotion(){var r=gsap.timeline();return r.fromTo(".circle",{scale:0},{scale:1,duration:.25,stagger:.25},"PlayMotion").fromTo("#Curves",{drawSVG:"0%"},{duration:1,drawSVG:"100% 0%"},"PlayMotion"),r}function patternMotion(){var r=gsap.timeline();return r.from(".odd",{duration:2,drawSVG:0,stagger:.25,rotate:180,transformOrigin:"center"}).fromTo(".even",{drawSVG:"0% 0%"},{duration:2,drawSVG:"0% -100%",stagger:.25,rotate:180,transformOrigin:"center"},"spin"),r}function UIMotion(){return gsap.timeline()}gsap.registerPlugin(DrawSVGPlugin,GSDevTools);var mainTL=gsap.timeline();mainTL.add(simpleMotion()).add(patternMotion()).add(UIMotion()),GSDevTools.create();