import{gsap}from"gsap";import{DrawSVGPlugin}from"gsap/DrawSVGPlugin";import{GSDevTools}from"gsap/GSDevTools";function simpleMotion(){return gsap.timeline()}function patternMotion(){var r=gsap.timeline();return r.from(".odd",{duration:2,drawSVG:0,stagger:.25,rotate:180,transformOrigin:"center"}).fromTo(".even",{drawSVG:"0% 0%"},{duration:2,drawSVG:"0% -100%",stagger:.25,rotate:180,transformOrigin:"center"},"spin"),r}function UIMotion(){return gsap.timeline()}gsap.registerPlugin(DrawSVGPlugin,GSDevTools);var mainTL=gsap.timeline();mainTL.add(simpleMotion()).add(patternMotion()).add(UIMotion()),GSDevTools.create();